$.extend({
  alertBox: function(options) {
    var cover = $("<div class='cover'></div>"),
      text = textColor = fontSize = type = "";
    if (options instanceof Array) {
      text = options[0];
      type = options[1];
      textColor = options[2];
      fontSize = options[3];
    } else {
      text = options.text, type = options.type, textColor = options.textColor, fontSize = options.fontSize;
    }
    cover.appendTo($('body'));
    $("<div class='alertBox'><div class='alertText'></div><div class='alertBtn'>+</div></div>").appendTo($("body"));
    $(".alertText").html(text).addClass(type).css({ color: textColor, "font-size": fontSize });
    var fontSize = Number($(".alertText").css("fontSize").slice(0, -2)),
      textHeight = $(".alertText").outerHeight();
      console.log(fontSize+','+textHeight);
    $(".alertText").css("lineHeight", fontSize + 8 + "px");
    if (fontSize == textHeight) {
      $(".alertText").addClass("textCenter");
    }
    $(".alertBox").css("visibility", "visible");
    $(".alertBtn").addClass("activeBtn");
    $('.alertBtn').on('click', function() {
      $(this).removeClass("activeBtn");
      $(this).parent('').fadeOut("fast", function() {
        $(".cover").fadeOut("fast");
      });
    });
  }
});
